import React, { useState, useEffect } from 'react';
import './App.css';
import {Badge, Button} from 'react-bootstrap'


function App() {

  const [wiches, setWiches] = useState([])
  const[wichNumber, setWichNumber] = useState(0)
  const [wichText, setWichText] = useState("")

  const swipeClicked = (posVote) => {
    const sand_id = wiches[wichNumber]['_id']
    console.log('/votewich/' + sand_id + "/" + posVote ? "yes" : "no")
    fetch('/votewich/' + sand_id + "/" + (posVote ? "yes" : "no"), {method: "PUT"})
  }

  // gets list of sandwiches
  useEffect(() => {
    fetch('/getwich')
      .then(response => response.json())
      .then(data => setWiches(data))
      .then(data => console.log(data))
      .catch(err => console.log(err))
  }, [])


  //updates the wich text
  useEffect(() => {
    if (wiches.length > 0) {
      const wich = wiches[wichNumber]
      var text = "A "
      for (var i = 0; i < wich['mains'].length; i++) {
        text += wich['mains'][i] + " "
        if (i < wich['mains'].length - 1) {
          text += "and "
        }
      }
      text += "sandwich with "
      for (i = 0; i < wich['condiments'].length; i++) {
        text += wich['condiments'][i] + " "
        if (i < wich['condiments'].length - 1) {
          text += "and "
        }
      }
      text += "on "
      text += wich['bread'] + "."
      setWichText(text)
    }
  })

  if (wiches.length === 0) {
    return (
      <div id="not-loading">
        <p>Not loading. Please refresh or try again later</p>
      </div>
    )
  }


  return (
    <div className="App">
        <p>{wichText}</p>
        <Button variant="outline-success" onChange={() => swipeClicked(true)}>I'd eat that. Swipe Right!</Button>
        <Button variant="outline-danger" onChange={() => swipeClicked(false)}>Yucky. Swipe Left!</Button>

    </div>
  );
}

export default App;
