import React, { useState, useEffect } from 'react';
import './App.css';
import {Badge} from 'react-bootstrap'


function App() {

  const [wiches, setWiches] = useState([])
  const[wichNumber, setWichNumber] = useState(0)
  const [wichText, setWichText] = useState("")
  useEffect(() => {
    fetch('/getwich')
      .then(response => response.json())
      .then(data => setWiches(data))
      .then(data => console.log(data))
      .catch(err => console.log(err))
  }, [])


  useEffect(() => {
    if (wiches.length > 0) {
      const wich = wiches[wichNumber]
      var text = "A "
      for (var i = 0; i < wich['mains'].length; i++) {
        text += wich['mains'][i] + " "
      }
      text += "sandwich with "
      for (var i = 0; i < wich['condiments'].length; i++) {
        text += wich['condiments'][i] + " "
        if (i < wich['condiments'].length) {
          text += "and "
        }
      }
      text += "on "
      text += wich['bread'] + "."
      setWichText(text)

    }
  })


  if (wiches.length == 0) {
    return (
      <div id="not-loading">
        <p>Not loading. Please refresh or try again later</p>
      </div>
    )
  }


  return (
    <div className="App">
        <p>{wichText}</p>
        <Badge variant="secondary">New</Badge>

    </div>
  );
}

export default App;
